import React from 'react'
import { Task } from '../Task/Task.component'
import { TaskForm } from '../Form/TaskForm.component'

const Tasks = ({ tasks }) => {

    return (
        <div>
            {tasks && tasks.map((task, index) => {
                return (
                    <div key={index} className='all-tasks'>
                        <Task task={task} />
                    </div>
                )
            })}
        </div>
    )
}

export default Tasks
