export const fetchAllTasks = async (setAllTasks, setTasksToDisplay) => {
    const res = await fetch("http://localhost:8000/tasks");
    const data = await res.json();
    setAllTasks(data);
    setTasksToDisplay(data);
}

const deleteTask = async (id) => {

    const res = await fetch(`http://localhost:8000/tasks/${id}`, { method: "delete" })

    if (res.status === 200) {
        return alert("Task Deleted");
    }

    return alert("Unable to Delete the task");
}
