import { useEffect, useState } from "react";
import "./TaskFrom.styles.scss";

export const TaskForm = ({ setDisplayForm }) => {

    const [formFields, setFormFields] = useState({});

    const changeHandler = (e) => {

        const { name, value } = e.target;

        console.log(name, value)

        return setFormFields[{ ...formFields, [name]: value }]
    }

    const submitHandler = (e) => {
        e.preventDefault();
        console.log(formFields);

    }

    useEffect(() => {

    }, [])

    return (
        <div className="task-form-wrapper">
            <div className="task-form">
                <div className="hide-form-btn">
                    <button onClick={() => setDisplayForm(false)}>
                        X
                    </button>
                </div>

                <form onSubmit={submitHandler}>
                    <div className="form-field">
                        <label>Title</label>
                        <input type="text" name="title" required onChange={changeHandler} />
                    </div>
                    <div className="form-field">
                        <label>Due Date</label>
                        <input type="date" name="dueDate" required onChange={changeHandler} />
                    </div>

                    <div className="form-field">
                        <label>Description</label>
                        <textarea name="description" rows="5" required onChange={changeHandler} ></textarea>
                    </div>

                    <div className="form-submit-btn">
                        <button type="submit">
                            Add
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}
