import "./Home.styles.scss";
import { TaskForm } from "../Form/TaskForm.component";
import { useContext, useEffect, useState } from "react";
import Tasks from "../Tasks/Tasks.component";
import { TasksContext } from "../../context/tasks.context";

export const Home = () => {

    const [displayForm, setDisplayForm] = useState(false);
    // const [allTasks, setAllTasks] = useState();

    const [searchText, setSearchText] = useState();

    const { tasks } = useContext(TasksContext);

    const [tasksToDisplay, setTasksToDisplay] = useState(tasks);



    useEffect(() => {
        const fetchAllTasks = async () => {
            const res = await fetch("http://localhost:8000/tasks");
            const data = await res.json();
            setAllTasks(data);
            setTasksToDisplay(tasks);
        }

        fetchAllTasks();

    }, [displayForm])

    // useEffect(() => {
    //     if (allTasks) {
    //         setTasksToDisplay(allTasks.filter((task) => task.title.toLowerCase().includes(searchText)))
    //     }
    // }, [searchText])

    useEffect(() => {
        console.log(tasks)
    }, [])

    const getSearchText = (e) => {
        return setSearchText(e.target.value.toLowerCase());
    }

    return (
        <div className='home'>
            <div className="header-wrapper">
                <header>
                    <h2>Todo App</h2>
                    <form>
                        <input type="text" onChange={getSearchText} placeholder="Search Task.." />
                        {/* <button type="submit">
                            <img src={searchIcon} alt="" />
                        </button> */}
                    </form>
                </header>
            </div>

            <div className="body">
                <h1>Tasks</h1>
                <div className="task-add-btn">
                    <button onClick={() => setDisplayForm(true)}>
                        <span>+</span> Add Task
                    </button>
                </div>

                {
                    displayForm && <TaskForm setDisplayForm={setDisplayForm} />
                }

                {
                    tasksToDisplay && <Tasks tasks={tasks} />
                }

            </div>
        </div>
    )
}
