import "./Home.styles.scss";
import searchIcon from '../../assests/search-icon.png';
import { TaskForm } from "../Form/TaskForm.component";
import { useEffect, useState } from "react";
import Tasks from "../Tasks/Tasks.component";

export const Home = () => {

    const [displayForm, setDisplayForm] = useState(false);
    const [allTasks, setAllTasks] = useState();
    const [tasksToDisplay, setTasksToDisplay] = useState();
    const [searchText, setSearchText] = useState();

    useEffect(() => {
        const fetchAllTasks = async () => {
            const res = await fetch("http://localhost:8000/tasks");
            const data = await res.json();
            setAllTasks(data);
            setTasksToDisplay(data);
        }

        fetchAllTasks();

    }, [displayForm])

    useEffect(() => {
        if (allTasks) {
            setTasksToDisplay(allTasks.filter((task) => task.title.includes(searchText)))
            co
        }
    }, [allTasks, searchText])

    return (
        <div className='home'>
            <div className="header-wrapper">
                <header>
                    <h2>Todo App</h2>
                    <form>
                        <input type="text" />
                        <button type="submit">
                            <img src={searchIcon} alt="" />
                        </button>
                    </form>
                </header>
            </div>

            <div className="body">
                <h1>Tasks</h1>
                <div className="task-add-btn">
                    <button onClick={() => setDisplayForm(true)}>
                        <span>+</span> Add Task
                    </button>
                </div>

                {
                    displayForm && <TaskForm setDisplayForm={setDisplayForm} />
                }

                {
                    tasksToDisplay && <Tasks tasks={tasksToDisplay} />
                }

            </div>
        </div>
    )
}
